sampleQueries.query:
let startOfDay = datetime_part("Hour", datetime("09:00"));
 let endOfDay = datetime_part("Hour", datetime("17:00"));
 let saturday = time(6);
 let sunday = time(0);
 ESETEnterpriseInspector_CL
 | where tolower(ruleName_s) contains "remote user login" or tolower(ruleName_s) contains "detected rdp communication"
 | where datetime_part("Hour", creationTime_t) !between(startOfDay .. endOfDay)
 or dayofweek(creationTime_t) == saturday or dayofweek(creationTime_t) == sunday
