body.messageBody:
{
    "type": "AdaptiveCard",
    "body": [
        {
            "type": "TextBlock",
            "size": "Large",
            "weight": "Bolder",
            "text": "Suspicious IP - Azure Sentinel",
            "wrap": true
        },
        {
            "type": "TextBlock",
            "text": "@{triggerBody()?['object']?['properties']?['severity']} incident @{triggerBody()?['object']?['properties']?['title']}",
            "wrap": true,
            "weight": "Bolder"
        },
        {
            "type": "TextBlock",
            "text": "Incident description",
            "wrap": true,
            "weight": "Bolder"
        },
        {
            "type": "TextBlock",
            "text": "@{triggerBody()?['object']?['properties']?['description']}",
            "wrap": true
        },
        {
            "type": "TextBlock",
            "text": "Alert product names",
            "wrap": true,
            "weight": "Bolder"
        },
        {
            "type": "TextBlock",
            "text": "@{join(triggerBody()?['object']?['properties']?['additionalData']?['alertProductNames'], ', ')}",
            "wrap": true
        },
        {
            "type": "TextBlock",
            "text": "[Click here to view the Incident](@{triggerBody()?['object']?['properties']?['incidentUrl']})",
            "wrap": true
        },
        {
            "type": "ColumnSet",
            "columns": [
                {
                    "type": "Column",
                    "items": [
                        {
                            "type": "TextBlock",
                            "size": "Medium",
                            "weight": "Bolder",
                            "text": "Incident configuration:",
                            "wrap": true
                        }
                    ],
                    "width": "auto"
                }
            ]
        },
        {
            "type": "ColumnSet",
            "columns": [
                {
                    "type": "Column",
                    "items": [
                        {
                            "type": "Image",
                            "style": "Person",
                            "url": "https://connectoricons-prod.azureedge.net/releases/v1.0.1391/1.0.1391.2130/azuresentinel/icon.png",
                            "size": "Small"
                        }
                    ],
                    "width": "auto"
                }
            ]
        },
        {
            "type": "TextBlock",
            "text": "Close Azure Sentinal incident?"
        },
        {
            "choices": [
                {
                    "title": "False Positive - Inaccurate Data",
                    "value": "False Positive - Inaccurate Data"
                },
                {
                    "title": "False Positive - Incorrect Alert Logic",
                    "value": "False Positive - Incorrect Alert Logic"
                },
                {
                    "title": "True Positive - Suspicious Activity",
                    "value": "True Positive - Suspicious Activity"
                },
                {
                    "title": "Benign Positive - Suspicious But Expected",
                    "value": "Benign Positive - Suspicious But Expected"
                },
                {
                    "title": "Undetermined",
                    "value": "Undetermined"
                }
            ],
            "id": "incidentStatus",
            "style": "compact",
            "type": "Input.ChoiceSet",
            "value": "Benign Positive - Suspicious But Expected"
        },
        {
            "type": "TextBlock",
            "text": "Change Azure Sentinel Incident Severity?"
        },
        {
            "choices": [
                {
                    "title": "High",
                    "value": "High"
                },
                {
                    "title": "Medium",
                    "value": "Medium"
                },
                {
                    "title": "Low",
                    "value": "Low"
                },
                {
                    "title": "Informational",
                    "value": "Informational"
                }
            ],
            "id": "incidentSeverity",
            "style": "compact",
            "type": "Input.ChoiceSet",
            "value": "@{triggerBody()?['object']?['properties']?['severity']}"
        },
        {
            "type": "TextBlock",
            "text": "Cisco Firepower Actions",
            "weight": "Bolder"
        },
        {
            "type": "Image",
            "url": "data:image/png;base64,",
            "size": "Medium"
        },
        {
            "type": "Container",
            "items": @{variables('adaptiveCardIPAddressItems')}
        }
    ],
    "actions": [
        {
            "type": "Action.Submit",
            "title": "Submit"
        },
        {
            "type": "Action.Submit",
            "title": "Ignore"
        }
    ],
    "$schema": "http://adaptivecards.io/schemas/adaptive-card.json",
    "version": "1.2"
}
