SlackAudit | extend evt_time = unixtime_seconds_todatetime(EventEndTime)
| where evt_time {TimeRange}
| where Action == "user_login"
|make-series Trend = count(SrcUserEmail) on evt_time from {TimeRange:start} to {TimeRange:end} step {TimeRange:grain} by SrcUserEmail;

SlackAudit | extend evt_time = unixtime_seconds_todatetime(EventEndTime)
| where evt_time {TimeRange}
| where Action == "user_login_failed"
|make-series Trend = count(SrcUserEmail) on evt_time from {TimeRange:start} to {TimeRange:end} step {TimeRange:grain} by SrcUserEmail;

let Guest_Created_list = 
SlackAudit | extend evt_time = unixtime_seconds_todatetime(EventEndTime)
| where evt_time {TimeRange}
| where Action == "guest_created" | summarize make_list(EntityUserEmail);
let Guest_Actions =
 SlackAudit | extend evt_time = unixtime_seconds_todatetime(EventEndTime)
 | where evt_time {TimeRange}
 | where ActorUserEmail in (Guest_Created_list)
 | extend UserAct = strcat(ActorUserEmail," - ",Action);
 Guest_Actions | make-series Trend = count() on evt_time from {TimeRange:start} to {TimeRange:end} step {TimeRange:grain} by UserAct

let Guest_Created_list = 
SlackAudit | extend evt_time = unixtime_seconds_todatetime(EventEndTime)
| where evt_time {TimeRange}
| where Action == "guest_created" | summarize make_list(EntityUserEmail);
let Guest_Actions =
 SlackAudit | extend evt_time = unixtime_seconds_todatetime(EventEndTime)
 | where evt_time {TimeRange}
| where ActorUserEmail in (Guest_Created_list);
 Guest_Actions | project Time=evt_time, GuestEmail=ActorUserEmail, Context_Domain_Location=ContextLocationDomain, IP=ContextIpAddress, ContextLocationName = ContextLocationName, Action, ActionDescription | sort by Time

