content.query:
let ExtraHopDetections = materialize(CommonSecurityLog
    | where DeviceVendor == "ExtraHop"
    | extend detectionID = DeviceCustomNumber1
    | extend StartTime = extract("start=([0-9-]+T[0-9:.]+Z)", 1, AdditionalExtensions,typeof(datetime))
    | project SourceIP, DestinationIP, detectionID, StartTime
    | summarize arg_max(todatetime(StartTime), *) by detectionID
    | sort by detectionID desc);
let t1 = ExtraHopDetection | where SourceIP != "" | summarize dcount=dcount(detectionID) by SourceIP | project IPAddress=SourceIP, dcount;
let t2 = ExtraHopDetection | where DestinationIP != "" | summarize dcount=dcount(detectionID) by DestinationIP | project IPAddress=DestinationIP, dcount;
t1 | union t2 | summarize Count=sum(dcount) by ["IP Address"]=IPAddress | top 10 by Count desc
